const toggleButton=document.getElementById("theme-toggle");const htmlElement=document.documentElement;const themeIcon=document.getElementById("theme-icon");function updateIcon(){if(htmlElement.classList.contains("dark-mode")){themeIcon.textContent="☀️"}else{themeIcon.textContent="🌙"}}if(localStorage.getItem("dark-mode")==="enabled"){htmlElement.classList.add("dark-mode")}updateIcon();toggleButton.addEventListener("click",(()=>{htmlElement.classList.toggle("dark-mode");if(htmlElement.classList.contains("dark-mode")){localStorage.setItem("dark-mode","enabled")}else{localStorage.setItem("dark-mode","disabled")}updateIcon()}));