const ufo=document.createElement("div");ufo.id="ufo";ufo.textContent="ðŸ›¸";document.body.appendChild(ufo);const screenWidth=window.innerWidth;const screenHeight=window.innerHeight;function getRandomHeight(){return Math.random()*screenHeight}function getRandomSpeed(){return Math.random()*3+2}function moveUFO(){const randomHeight=getRandomHeight();const randomSpeed=getRandomSpeed();ufo.style.transitionDuration=`${randomSpeed}s`;ufo.style.transform=`translate(${screenWidth+100}px, ${randomHeight}px)`;setTimeout((()=>{ufo.style.transitionDuration="0s";ufo.style.transform=`translate(-100px, ${getRandomHeight()}px)`}),randomSpeed*1e3)}function startUFOAnimation(){function fly(){moveUFO();const randomDelay=Math.random()*4e3+2e3;const randomSpeed=parseFloat(ufo.style.transitionDuration)*1e3;setTimeout(fly,randomSpeed+randomDelay)}fly()}startUFOAnimation();